import React, { useState } from 'react';
import axios from 'axios';
import NavBar from './components/NavBar';
import ImageUploader from './components/ImageUploader';
import ImagePreview from './components/ImagePreview';
import ModelSelector from './components/ModelSelector';
import DiagnosisButton from './components/DiagnosisButton';
import DiagnosisResult from './components/DiagnosisResult';

function App() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [model, setModel] = useState(null);
  const [modelLoaded, setModelLoaded] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
    setPreview(URL.createObjectURL(e.target.files[0]));
  };

  const handleLoadModel = () => {
    setModelLoaded(true);
  };

  const handleShowDiagnosis = async () => {
    setLoading(true);
    const formData = new FormData();
    formData.append('image', image);
    const res = await axios.post(`${process.env.REACT_APP_API}/api/v1/image`, formData);
    setResult(res.data);
    setLoading(false);
  };

  return (
    <div>
      <NavBar />
      <ImageUploader onImageChange={handleImageChange} />
      <ImagePreview preview={preview} />
      <ModelSelector onSelect={setModel} />
      <DiagnosisButton modelLoaded={modelLoaded} onClick={modelLoaded ? handleShowDiagnosis : handleLoadModel} loading={loading} />
      <DiagnosisResult result={result} />
    </div>
  );
}

export default App;
